import styled from '@emotion/styled';
import theme from '@/app/theme';

const Shape = styled.div`
  position: relative;
  display: flex;
  justify-content: center;
  align-items: flex-end;
  width: 40px;
  height: 40px;

  svg {
    width: 100%;
    height: 100%;
  }

  span {
    position: absolute;
    top: 44px;
    visibility: hidden;
    opacity: 0;
    background-color: #333;
    font-family: ${theme.fonts.primary};
    font-size: .75rem;
    color: #fff;
    padding: 4px 8px;
    border-radius: 4px;

    white-space: nowrap;
    transition: opacity 300ms ease;
    z-index: 10;
  }

  &:hover span {
    visibility: visible;
    opacity: 1;
  }
`;

const CheckLabel = styled.label`
  display: flex;
  justify-content: center;
  align-items: center;
  cursor: pointer;
  user-select: none;

  input {
    position: absolute;
    opacity: 0;
    width: 0;
    height: 0;
  }

  input + div {
    outline: 1px solid transparent;
    border: 4px solid transparent;
    border-radius: 50%;
    transition: 300ms ease;

    &:hover {
      background: rgba(97, 97, 97, .15);
      border: 4px solid transparent;
    }
  }

  input:checked + div {
    outline-color: #333;  
  }
`;

const CheckButton = ({ isChecked, handleChange, name, children }) => {
  return (
    <CheckLabel>
      <input 
        type="checkbox" 
        checked={isChecked} 
        onChange={(e) => handleChange(e, name)}
      />
      { children }
    </CheckLabel>
  )
};

const ShapeWrapper = ({ name, isChecked, handleChange, children }) => {
  return (
    <CheckButton name={name} isChecked={isChecked} handleChange={handleChange}>
      <Shape>
        { children }
        <span>{name}</span>
      </Shape>
    </CheckButton>
  )
}

